            ]
        }
    }
]

matrix_sum
select matrix_sum(matrix_dense(array(array(1.0, 2.0, 3.0), array(2.0, 3.0, 4.0))), 0)

[
    {
        "UDF:matrix_sum(UDF:matrix_dense(cast(array(array(1.0, 2.0, 3.0), array(2.0, 3.0, 4.0)) as array>)), 0)": {
            "type": 1,
            "values": [
                3.0,
                5.0,
                7.0
            ]
        }
    }
]


